package com.example.trackit;

import static org.junit.jupiter.api.Assertions.*;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import java.util.ArrayList;

class EventTest {
    Event event;

    @BeforeEach
    private void mockEvent() {
        String comment = "Hey what a great run!";
        String encodedImage = "";
        String date = "12/01/2021";

        event = new Event(comment, encodedImage, date);

    }

    @Test
    void testEventComment() {
        //testing getter for habit name
        String comment = "Hey what a great run!";
        assertEquals(event.getComment(), comment);

        //testing setter for habit name
        String comment2 = "Running is OP";
        event.setComment(comment2);
        assertEquals(comment2, event.getComment());
    }

    
}